function SertyModuleUI.SetScaleformParams(scaleform, data)
    data = data or {}
    for k, v in pairs(data) do
        PushScaleformMovieFunction(scaleform, v.name)
        if v.param then
            for _, par in pairs(v.param) do
                if math.type(par) == "integer" then
                    PushScaleformMovieFunctionParameterInt(par)
                elseif type(par) == "boolean" then
                    PushScaleformMovieFunctionParameterBool(par)
                elseif math.type(par) == "float" then
                    PushScaleformMovieFunctionParameterFloat(par)
                elseif type(par) == "string" then
                    PushScaleformMovieFunctionParameterString(par)
                end
            end
        end
        if v.func then
            v.func()
        end
        PopScaleformMovieFunctionVoid()
    end
end

---Visible
---@param Menu function
---@param Value boolean
---@return table
---@public
function SertyModuleUI.Visible(Menu, Value)
    if Menu ~= nil then
        if Menu() then
            if type(Value) == "boolean" then
                if Value then
                    if SertyModuleUI.CurrentMenu ~= nil then
                        SertyModuleUI.CurrentMenu.Closed()
                        SertyModuleUI.CurrentMenu.Open = not Value
                    end
                    Menu:UpdateInstructionalButtons(Value);
                    Menu:UpdateCursorStyle();
                    SertyModuleUI.CurrentMenu = Menu
                    menuOpen = true
                else
                    menuOpen = false
                    SertyModuleUI.CurrentMenu = nil
                end
                Menu.Open = Value
                SertyModuleUI.Options = 0
                SertyModuleUI.ItemOffset = 0
                SertyModuleUI.LastControl = false
            else
                return Menu.Open
            end
        end
    end
end 

function SertyModuleUI.CloseAll()
    menuOpen = false

    if SertyModuleUI.CurrentMenu ~= nil then
        local parent = SertyModuleUI.CurrentMenu.Parent
        while parent ~= nil do
            parent.Index = 1
            parent.Pagination.Minimum = 1
            parent.Pagination.Maximum = 10
            parent = parent.Parent
        end
        SertyModuleUI.CurrentMenu.Index = 1
        SertyModuleUI.CurrentMenu.Pagination.Minimum = 1
        SertyModuleUI.CurrentMenu.Pagination.Maximum = 10
        SertyModuleUI.CurrentMenu.Open = false
        SertyModuleUI.CurrentMenu = nil
    end
    SertyModuleUI.Options = 0
    SertyModuleUI.ItemOffset = 0
    ResetScriptGfxAlign()
end

