---Subtitle
---@return nil
---@public
function SertyModuleUI.Subtitle()
    if SertyModuleUI.CurrentMenu ~= nil then
        if SertyModuleUI.CurrentMenu() then
            SertyModuleUI.ItemsSafeZone(SertyModuleUI.CurrentMenu)
            if SertyModuleUI.CurrentMenu.Subtitle ~= "" then
                RenderRectangle(SertyModuleUI.CurrentMenu.X, SertyModuleUI.CurrentMenu.Y + SertyModuleUI.ItemOffset, SertyModuleUI.Settings.Items.Subtitle.Background.Width + SertyModuleUI.CurrentMenu.WidthOffset, SertyModuleUI.Settings.Items.Subtitle.Background.Height + SertyModuleUI.CurrentMenu.SubtitleHeight, 0, 0, 0, 255)
                RenderText(SertyModuleUI.CurrentMenu.Subtitle, SertyModuleUI.CurrentMenu.X + SertyModuleUI.Settings.Items.Subtitle.Text.X, SertyModuleUI.CurrentMenu.Y + SertyModuleUI.Settings.Items.Subtitle.Text.Y + SertyModuleUI.ItemOffset, 0, SertyModuleUI.Settings.Items.Subtitle.Text.Scale, 245, 245, 245, 255, nil, false, false, SertyModuleUI.Settings.Items.Subtitle.Background.Width + SertyModuleUI.CurrentMenu.WidthOffset)
                if SertyModuleUI.CurrentMenu.Index > SertyModuleUI.CurrentMenu.Options or SertyModuleUI.CurrentMenu.Index < 0 then
                    SertyModuleUI.CurrentMenu.Index = 1
                end
                SertyModuleUI.RefreshPagination()
                if SertyModuleUI.CurrentMenu.PageCounter == nil then
                    RenderText(SertyModuleUI.CurrentMenu.PageCounterColour .. SertyModuleUI.CurrentMenu.Index .. " / " .. SertyModuleUI.CurrentMenu.Options, SertyModuleUI.CurrentMenu.X + SertyModuleUI.Settings.Items.Subtitle.PreText.X + SertyModuleUI.CurrentMenu.WidthOffset, SertyModuleUI.CurrentMenu.Y + SertyModuleUI.Settings.Items.Subtitle.PreText.Y + SertyModuleUI.ItemOffset, 0, SertyModuleUI.Settings.Items.Subtitle.PreText.Scale, 245, 245, 245, 255, 2)
                else
                    RenderText(SertyModuleUI.CurrentMenu.PageCounter, SertyModuleUI.CurrentMenu.X + SertyModuleUI.Settings.Items.Subtitle.PreText.X + SertyModuleUI.CurrentMenu.WidthOffset, SertyModuleUI.CurrentMenu.Y + SertyModuleUI.Settings.Items.Subtitle.PreText.Y + SertyModuleUI.ItemOffset, 0, SertyModuleUI.Settings.Items.Subtitle.PreText.Scale, 245, 245, 245, 255, 2)
                end
                SertyModuleUI.ItemOffset = SertyModuleUI.ItemOffset + SertyModuleUI.Settings.Items.Subtitle.Background.Height
            end
        end
    end
end

