---Render
---@param instructionalButton boolean
---@return nil
---@public
function SertyModuleUI.Render(instructionalButton)
    if SertyModuleUI.CurrentMenu ~= nil then
        if SertyModuleUI.CurrentMenu() then
            if SertyModuleUI.CurrentMenu.Safezone then
                ResetScriptGfxAlign()
            end
            if (instructionalButton) then
                DrawScaleformMovieFullscreen(SertyModuleUI.CurrentMenu.InstructionalScaleform, 255, 255, 255, 255, 0)
            end
            SertyModuleUI.CurrentMenu.Options = SertyModuleUI.Options
            SertyModuleUI.CurrentMenu.SafeZoneSize = nil
            SertyModuleUI.Controls()
            SertyModuleUI.Options = 0
            SertyModuleUI.StatisticPanelCount = 0
            SertyModuleUI.ItemOffset = 0
            if SertyModuleUI.CurrentMenu.Controls.Back.Enabled and SertyModuleUI.CurrentMenu.Closable then
                if SertyModuleUI.CurrentMenu.Controls.Back.Pressed then
                    SertyModuleUI.CurrentMenu.Controls.Back.Pressed = false
                    local Audio = SertyModuleUI.Settings.Audio
                    SertyModuleUI.PlaySound(Audio[Audio.Use].Back.audioName, Audio[Audio.Use].Back.audioRef)
                    if SertyModuleUI.CurrentMenu.Closed ~= nil then
                        collectgarbage()
                        SertyModuleUI.CurrentMenu.Closed()
                    end
                    if SertyModuleUI.CurrentMenu.Parent ~= nil then
                        if SertyModuleUI.CurrentMenu.Parent() then
                            SertyModuleUI.NextMenu = SertyModuleUI.CurrentMenu.Parent
                            SertyModuleUI.CurrentMenu:UpdateCursorStyle()
                        else
                            --print('xxx') Debug print
                            SertyModuleUI.NextMenu = nil
                            SertyModuleUI.Visible(SertyModuleUI.CurrentMenu, false)
                        end
                    else
                        --print('zz') Debug print
                        SertyModuleUI.NextMenu = nil
                        SertyModuleUI.Visible(SertyModuleUI.CurrentMenu, false)
                    end
                end
            end
            if SertyModuleUI.NextMenu ~= nil then
                if SertyModuleUI.NextMenu() then
                    SertyModuleUI.Visible(SertyModuleUI.CurrentMenu, false)
                    SertyModuleUI.Visible(SertyModuleUI.NextMenu, true)
                    SertyModuleUI.CurrentMenu.Controls.Select.Active = false
                    SertyModuleUI.NextMenu = nil
                    SertyModuleUI.LastControl = false
                end
            end
        end
    end
end

---ItemsDescription
---@param CurrentMenu table
---@param Description string
---@param Selected boolean
---@return nil
---@public
function SertyModuleUI.ItemsDescription(CurrentMenu, Description, Selected)
    ---@type table
    if Description ~= "" or Description ~= nil then
        local SettingsDescription = SertyModuleUI.Settings.Items.Description;
        if Selected and CurrentMenu.Description ~= Description then
            CurrentMenu.Description = Description or nil
            ---@type number
            local DescriptionLineCount = GetLineCount(CurrentMenu.Description, CurrentMenu.X + SettingsDescription.Text.X, CurrentMenu.Y + SettingsDescription.Text.Y + CurrentMenu.SubtitleHeight + SertyModuleUI.ItemOffset, 0, SettingsDescription.Text.Scale, 255, 255, 255, 255, nil, false, false, SettingsDescription.Background.Width + (CurrentMenu.WidthOffset - 5.0))
            if DescriptionLineCount > 1 then
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height * DescriptionLineCount
            else
                CurrentMenu.DescriptionHeight = SettingsDescription.Background.Height + 7
            end
        end
    end
end

---MouseBounds
---@param CurrentMenu table
---@param Selected boolean
---@param Option number
---@param SettingsButton table
---@return boolean
---@public
function SertyModuleUI.ItemsMouseBounds(CurrentMenu, Selected, Option, SettingsButton)
    ---@type boolean
    local Hovered = false
    Hovered = SertyModuleUI.IsMouseInBounds(CurrentMenu.X + CurrentMenu.SafeZoneSize.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SafeZoneSize.Y + CurrentMenu.SubtitleHeight + SertyModuleUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height)
    if Hovered and not Selected then
        RenderRectangle(CurrentMenu.X, CurrentMenu.Y + SettingsButton.Rectangle.Y + CurrentMenu.SubtitleHeight + SertyModuleUI.ItemOffset, SettingsButton.Rectangle.Width + CurrentMenu.WidthOffset, SettingsButton.Rectangle.Height, 255, 255, 255, 20)
        if CurrentMenu.Controls.Click.Active then
            CurrentMenu.Index = Option
            local Audio = SertyModuleUI.Settings.Audio
            SertyModuleUI.PlaySound(Audio[Audio.Use].Error.audioName, Audio[Audio.Use].Error.audioRef)
        end
    end
    return Hovered;
end

---ItemsSafeZone
---@param CurrentMenu table
---@return nil
---@public
function SertyModuleUI.ItemsSafeZone(CurrentMenu)
    if not CurrentMenu.SafeZoneSize then
        CurrentMenu.SafeZoneSize = { X = 0, Y = 0 }
        if CurrentMenu.Safezone then
            CurrentMenu.SafeZoneSize = SertyModuleUI.GetSafeZoneBounds()
            SetScriptGfxAlign(76, 84)
            SetScriptGfxAlignParams(0, 0, 0, 0)
        end
    end
end

function SertyModuleUI.CurrentIsEqualTo(Current, To, Style, DefaultStyle)
    if (Current == To) then
        return Style;
    else
        return DefaultStyle or {};
    end
end

function SertyModuleUI.RefreshPagination()
    if (SertyModuleUI.CurrentMenu ~= nil) then
        if (SertyModuleUI.CurrentMenu.Index > 10) then
            local offset = SertyModuleUI.CurrentMenu.Index - 10
            SertyModuleUI.CurrentMenu.Pagination.Minimum = 1 + offset
            SertyModuleUI.CurrentMenu.Pagination.Maximum = 10 + offset
        else
            SertyModuleUI.CurrentMenu.Pagination.Minimum = 1
            SertyModuleUI.CurrentMenu.Pagination.Maximum = 10
        end
    end
end

function SertyModuleUI.IsVisible(menu, header, glare, instructional, items, panels)
    if (SertyModuleUI.Visible(menu)) then
        if (header == true) then
            SertyModuleUI.Banner(true, glare or false)
        end
        SertyModuleUI.Subtitle()
        if (items ~= nil) then
            items()
        end
        SertyModuleUI.Background();
        SertyModuleUI.Navigation();
        SertyModuleUI.Description();
        if (panels ~= nil) then
            panels()
        end
        SertyModuleUI.Render(instructional or false)
    end
end


---CreateWhile
---@param wait number
---@param menu table
---@param key number
---@param closure function
---@return void
---@public
function SertyModuleUI.CreateWhile(wait, menu, key, closure)
    Citizen.CreateThread(function()
        while (true) do
            Wait(wait or 0.1)

            if (key ~= nil) then
                if IsControlJustPressed(1, key) then
                    SertyModuleUI.Visible(menu, not SertyModuleUI.Visible(menu))
                end
            end

            closure()
        end
    end)
end

